mapper.select:
SELECT course_id,subject_id,course_name,course_name,provider_name
FROM course
WHERE subject_id=#{subjectId}
LIMIT #{offset},#{limit}

SELECT course_id,subject_id,provider_id,course_name,course_name,provider_name
FROM course
WHERE provider_id=#{provider_id}
LIMIT #{offset},#{limit}

SELECT course_id,subject_id,teacher_id,course_name,course_name,provider_name
FROM course
WHERE teacher_id=#{teacherId}
LIMIT #{offset},#{limit}
